/**
 * Key for "CORRECT" results, denoted by 游릴
 *
 * @type {"correct"}
 */
export const CORRECT_KEY = "correct";
/**
 * Key for "MISTAKE" results, denoted by 游릳
 *
 * @type {"mistake"}
 */
export const MISTAKE_KEY = "mistake";
/**
 * Key for "HINT" results, denoted by 游리
 *
 * @type {"hint"}
 */
export const HINT_KEY = "hint";
/**
 * Key for "DOUBLE HINT" results, denoted by 游
 *
 * @type {"doubleHint"}
 */
export const DOUBLE_HINT_KEY = "doubleHint";

/**
 * Dictionary to map the icon (generated by cluesbysam) to the key (used internally)
 *
 * @type {{ "游릴": string; "游릳": string; "游리": string; "游": string; }}
 */
export const iconMap = {
  "游릴": CORRECT_KEY,
  ":large_green_square:": CORRECT_KEY,
  "游릳": MISTAKE_KEY,
  ":large_yellow_square:": MISTAKE_KEY,
  "游리": HINT_KEY,
  ":large_yellow_circle:": HINT_KEY,
  "游": DOUBLE_HINT_KEY,
  ":large_orange_circle:": DOUBLE_HINT_KEY,
};

// Define standard patterns for emoji categories
export const CORRECT_REGEX = /游릴|:large_green_square:/;
export const MISTAKE_REGEX = /游릳|:large_yellow_square:/;
export const HINT_REGEX = /游리|:large_yellow_circle:/;
export const DOUBLE_HINT_REGEX = /游맢:large_orange_circle:/;
